class Solution {
    public int uniqueMorseRepresentations(String[] words) {
        Set<String> uniqueMorse = new HashSet();
        String[] morseCodes = {".-","-...","-.-.","-..",".","..-.","--.","....","..",".---","-.-",".-..","--","-.","---",".--.","--.-",".-.","...","-","..-","...-",".--","-..-","-.--","--.."};
        for(String word: words){
            String code="";
            for(int i =0; i<word.length();i++){
                int n = word.charAt(i) - 'a';
                code+=morseCodes[n];
            }
            uniqueMorse.add(code);
        }
        return uniqueMorse.size();
    }
}
