class Solution {
    public String pushDominoes(String dominoes) {
        String res = "", intermediate="", intermediateDots="";
        int i = 0, j;
        int length = dominoes.length();
        while(i<length){
            char ch = dominoes.charAt(i);
            if (ch=='L' || ch=='R')
            {   res += Character.toString(ch);
                i++;}
            else{
                if(i>0 && dominoes.charAt(i-1)=='R'){
                    j=i;
                    intermediate="";
                    while(j<length && dominoes.charAt(j)=='.'){
                        intermediate+="R";
                        j+=1;
                    }
                    int count = j-i;
                    if (j==length || dominoes.charAt(j)=='R')
                        res += intermediate;
                    else
                        res += "R".repeat(count/2) + ".".repeat(count-2*(count/2)) + "L".repeat(count/2);
                    i=j;
                }
                else{
                    j=i;
                    intermediateDots="";
                    intermediate="";
                    while(j<length && dominoes.charAt(j)=='.'){
                        intermediateDots+=".";
                        intermediate+="L";
                        j+=1;
                    }
                    int count = j-i;
                    if (j==length || dominoes.charAt(j)=='R')
                        res += intermediateDots;
                    else
                        res += intermediate;
                    i=j;
                }
            }

        }
        return res;
    }
}
