    int binarysearch(vector<int>&l,int pos)
    {
        int e=l.size()-1,s=0,index=-1;
        while(s<=e)
        {
            int mid=s+(e-s)/2;
            if(l[mid]>pos)
            {
                index=mid;
                e=mid-1;
            }else s=mid+1;
        }
        return index;
