class Solution {
public:
    string pushDominoes(string dominoes) {
        int n = dominoes.size();
        vector<int>distanceOfL(n, 0), distanceofR(n, 0);
    // we traverse from left to right as only the R which are in the left position will affect any dominoes[i] == '.'
        for(int i = 0; i < n; i++){
            if(dominoes[i] == 'R'){
                distanceofR[i] = 0;
            }
            // we are checking this because if dominoes[i] == 'L', then we need to cut off the affect of the R 
			//which was present on the left side of the current dominoes[i]
            else if(dominoes[i] == 'L'){
                distanceofR[i] = 1e9;
            }
            else{
                if(i > 0 && distanceofR[i - 1] != 1e9){
                    distanceofR[i] = distanceofR[i - 1] + 1;
                }
                else{
                     distanceofR[i] = 1e9;
                }
            }
        }
        // we are traversing from right as only the L which are in right position will affect any dominoes[i] == '.'
        for(int i = n - 1; i >= 0; i--){
            if(dominoes[i] == 'L'){
                distanceOfL[i] = 0;
            }
             // we are checking this because if dominoes[i] == R', then we need to cut off the affect of the L
			 //which was present on the right side of the current dominoes[i]
            else if(dominoes[i] == 'R'){
                distanceOfL[i] = 1e9;
            }
            else{
                if(i < n - 1 && distanceOfL[i + 1] != 1e9){
                    distanceOfL[i] = distanceOfL[i + 1] + 1;
                }
                else{
                    distanceOfL[i] = 1e9;
                }
            }
        }
        for(int i = 0; i < n; i++){
            if(distanceOfL[i] < distanceofR[i]){
                dominoes[i] = 'L';
            }
            else if(distanceofR[i] < distanceOfL[i]){
                dominoes[i] = 'R';
            }
           // When distanceofR[i] == distanceOfL[i], dominoes[i] will be '.' 
