                else if(j==n-1 && dom[j]=='.'){
                    if(dom[i]=='R'){
                        solD=1;
                        break;
                    }
                }
                j++;
            }
            // cout<<endl;
            if(solA==1){
                // inserting L
                for(int k=i;k<j && k<n;k++) dom[k]='L';
            }
            if(solB==1){
                // inserting R
                for(int k=i;k<j && k<n;k++) dom[k]='R';
            }
            if(solC==1){
                int k=i+(j-i-1)/2;
                for(int z=i;z<=k && z<n;z++) dom[z]='R';
                for(int z=j;z>=(j-k+i) && z>0;z--) dom[z]='L';
            }
            if(solD==1){
                for(int z=i;z<n;z++) dom[z]='R';
            }
            i=j;
        }
        return dom;
    }
};
