    void helper(vector<char> &arr, int j, int k){ // there are 4 conditions as follows
        if(arr[j]=='L' && arr[k]=='L') 
            for(int i=j; i<k; i++)
                arr[i]='L';

        else if(arr[j]=='R' && arr[k]=='R')
