class Solution {
public:
    string pushDominoes(string s) {
        int n = s.size(); 
        vector<pair<int,int>> dp(n,{0,0});
        // first domino to be dropped towards right , will be from left start!
       // beacuse all other dominoes next inline would depend on it!
        for(int i=0; i<n; i++) {
             if(s[i] == 'R') {
                  dp[i].second = 1;
             }else if(s[i] == '.' && i) {
                  dp[i].second = dp[i-1].second ? 1 + dp[i-1].second : 0;
             }
        }
        // first domino to be dropped towards left , will be from far right!
       // because all other dominoes next inline would depend on it!
        for(int i=n-1; i>=0; i--) {
              if(s[i] == 'L') {
                   dp[i].first = 1;
              }else if(s[i] == '.' && i+1<n) {
                   dp[i].first = dp[i+1].first ? 1 + dp[i+1].first : 0;
              }
        }
        for(int i=0; i<n; i++) {
             if(dp[i].second == 0 || dp[i].first == 0) {
                  s[i] = dp[i].second ? 'R' : dp[i].first ? 'L' : '.';  // current force direction!
             }else if(dp[i].first < dp[i].second) {
                  s[i] = 'L';   // if left has a force near than right direction!
             }else if(dp[i].second < dp[i].first) {
                  s[i] = 'R';  // if right has a force near than left direction!
             }
        }
        return s;
    }
};

