public class Solution 
{
    private IEnumerable<int> DistTo(IEnumerable<char> dominoes, char R, char L) 
    {
        int dist = Int32.MaxValue / 2;
        return dominoes
                .Select(domino => domino == R ? dist = 0 : (domino == L ? dist = Int32.MaxValue / 2 : ++dist))
                .Select(x => x < Int32.MaxValue / 2 ? x : Int32.MaxValue / 2);
    }

    public string PushDominoes(string dominoes)
        => String.Concat(DistTo(dominoes, 'R', 'L').Zip(DistTo(dominoes.Reverse(), 'L', 'R').Reverse(),
                                                        (r, l) => r < l ? 'R' : (r > l ? 'L' : '.')));
}
