        vector<ll> r(n), l(n);
        for(int i = 0; i < n; i++){
            if(s[i] == 'R') r[i] = 1;
            else if(s[i] == '.' and i > 0){
                if(r[i - 1] != INT_MAX) r[i] = r[i - 1] + 1;
                else r[i] = INT_MAX;
            }
            else r[i] = INT_MAX;     
