        for(int i = n - 1; i >= 0; i--){
            if(s[i] == 'L') l[i] = 1;
            else if(s[i] == '.' and i < n - 1){
                if(l[i + 1] != INT_MAX) l[i] = l[i + 1] + 1;
                else l[i] = INT_MAX;
