class Solution {
public:
    string pushDominoes(string dominoes) {
         int n  = dominoes.size();
         vector<pair<char,int>> v;
            for(int i=0;i<n;i++){
                    if(dominoes[i] == 'R') v.push_back({'R',i});
                    else if(dominoes[i] == 'L') v.push_back({'L',i});
            }
           if(v.size() == 0) return dominoes;
             if(v[0].first == 'L'){
                     for(int j = 0;j<=v[0].second;j++) dominoes[j] = 'L';
                 }
            for(int i=0;i<v.size()-1;i++){
                 if(v[i].first == 'R' && v[i+1].first == 'L'){
                        int k = v[i+1].second - v[i].second-1;
                        for(int j=0;j<k/2;j++){
                            dominoes[v[i].second + j + 1] = 'R';
                        } 
                        for(int j = k/2+1;j<=k;j++){
                            dominoes[v[i].second + j] = 'L';
                        }

                        if(k%2!=0) dominoes[v[i].second + (k+1)/2] = '.';
                 }
                 if(v[i].first == 'R' && v[i+1].first == 'R'){
                     for(int j=v[i].second;j<=v[i+1].second;j++) dominoes[j] = 'R';
                 }
                 if(v[i].first == 'L' && v[i+1].first == 'L'){
                     for(int j=v[i].second;j<=v[i+1].second;j++) dominoes[j] = 'L';
                 }

            }
            if(v[v.size() - 1].first == 'R'){
                for(int j=v[v.size()-1].second;j<n;j++) dominoes[j] = 'R';
            }
            return dominoes;
    }
};
