class Solution {
    private void replace(char[] d, char c, int a, int b){
        while(a < b){
            d[a++] = c;
        }
    }
    public String pushDominoes(String dominoes) {
        char[] d = dominoes.toCharArray();
        int prev = -1;
        for(int a = 0; a < d.length; a++){
            if(d[a] == 'L'){
                if(prev == -1 || d[prev] == 'L'){
                    replace(d, 'L', prev + 1, a);
                }else{
                    int diff = a - prev;
                    int mid = prev + diff / 2;
                    replace(d, 'R', prev + 1, mid + (diff % 2 == 1 ? 1 : 0));
                    replace(d, 'L', mid + 1, a);
                }
                prev = a;
            }else if(d[a] == 'R'){
                if(prev != -1 && d[prev] == 'R'){
                   replace(d, 'R', prev, a);
                }
                prev = a;
            }
        }
        if(prev != -1 && d[prev] == 'R') replace(d, 'R', prev + 1, d.length);
        return String.valueOf(d);
    }
}
