```
class Solution {
    public String pushDominoes(String s) {
        int n = s.length(), start=0, end=0;
        StringBuilder sb = new StringBuilder(s);

        while(end<n && s.charAt(end) == '.') end++;

		/// to take care of inputs starting with '.', like "...LRRL.LLRRL"
        if(end<n && s.charAt(start)=='.'){
            if(s.charAt(end) == 'L'){
                for(int i=start; i<end; i++) sb.setCharAt(i, 'L');
            }
            start = end;
        }

        while(end<n){
            if(s.charAt(end)=='.'){
                end++;
                continue;
            }

            else if(s.charAt(start) == 'L' && s.charAt(end) == 'R') start = end;

            else if(s.charAt(start) == 'R' && s.charAt(end) == 'L'){
                int len = (end-start-1);
                int size = len;
                size /= 2;

                for(int i=start; i<=(start+size); i++){
                    sb.setCharAt(i, 'R');
                }
                if(len % 2 == 0) for(int i=(start+size+1); i<end; i++) sb.setCharAt(i, 'L'); /// "R..L" -> "RRLL"
                else for(int i=(start+size+2); i<end; i++) sb.setCharAt(i, 'L'); /// "R...L" -> "RR.LL",

                start = end;
            }

            else if(s.charAt(start) == s.charAt(end)){
                for(int i=start+1; i<end; i++) sb.setCharAt(i, sb.charAt(start));
                start = end;
            }

            end++;
        }

		/// to take care of inputs ending with '.', like "LRRL..RR..R..."
        if(end == n && s.charAt(end-1)=='.'){
            if(s.charAt(start) == 'R'){
                for(int i=start+1; i<end; i++) sb.setCharAt(i, 'R');
                start = end;
            }
        }

        return sb.toString();
    }
}

