class Solution {
public:
    void cg(string& s,char c,int st,int ed){
        for(int i=st;i<=ed;i++)
            s[i] = c;
    }

    string pushDominoes(string dm) {
        int n = dm.size();
        int st = 0;
        char a,b;
        int ok = 0;
        while(ok<n && dm[ok]=='.')
            ok++;

        if(ok==n)
            return dm;

        if(dm[ok]=='L'){
            for(int i=0;i<=ok;i++)
                dm[i]='L';
        }


        a = dm[ok];
        st = ok;
        for(int i=ok+1;i<n;i++){

            while(i<n && dm[i]=='.')
                i++;

            if(i==n)
                i = n - 1;

            b = dm[i];

            if(a=='L' && b=='L')
                cg(dm,'L',st,i);
            else if(a=='R' && b=='R')
                cg(dm,'R',st,i);
            else if(a=='R' && b=='L'){
                int u = st,v =i;
                while(u<v)
                    dm[v--] = 'L',dm[u++] = 'R';

            }

            st = i;
            a = b;
        }

        int lst = n-1;
        char k;
        while(lst>=0){
            if(dm[lst]=='L' || dm[lst]=='R'){
                k = dm[lst];break;
            }
            lst--;
        }
        if(lst>=0 && k=='R')
            while(lst<n)
                dm[lst++] = k; 

        return dm;
    }
};
