var pushDominoes = function(dominoes) {
    dominoes = dominoes.split('');
    let l = 0, r = 0;
    while(r < dominoes.length){
        if(dominoes[r] === 'L') while(l <= r) dominoes[l++] = 'L';
        else if(dominoes[r] === 'R'){
            l = r;
            let f = 0;
            r++;
            while(r < dominoes.length) {
                if(dominoes[r] === 'R') {
                    f = 1;
                    break;
                }
                if(dominoes[r] === 'L') break;
                r++;
            }
            if(r === dominoes.length || f === 1) {
                while(l < r) dominoes[l++] = 'R';
                r--;
            }
            else {
                let dif = (r - l - 1) / 2 | 0;
                for(let i = 1; i <= dif; i++){
                    dominoes[l + i] = 'R';
                    dominoes[r - i] = 'L';
                }
                l = r + 1;
            }
        }
        r++;
    }
    return dominoes.join('');
};
