                        finalDominoes[j] = '.'; j += 1;
                    }
                    char remaining = (prev == 'L')? 'R': 'L';
                    while(finalDominoes[j] == '#') {
                        finalDominoes[j] = remaining;
                        j += 1;
                    }
                    i = j-1;
                }
            }
            prev = finalDominoes[i];
        }
        return finalDominoes;
    }
};
