class Solution {
public:
    string pushDominoes(string d) {
        vector<int> l(d.size(),-1);
        vector<int> r(d.size(),-1);
        int set = 0;
        int lset = 0;
        for(int i=0,j=d.size()-1;i<d.size();i++,j--) {
            if(d[i] == '.') {
                if(set == 0) l[i] = 0;
                else {
                    l[i] = set;
                    set++;
                }
            } else if(d[i] == 'R') {
                set = 1;
                l[i] = INT_MAX;
            } else if(d[i] == 'L') {
                set = 0;
                l[i] = INT_MAX;
            }
            if(d[j] == '.') {
                if(lset == 0) r[j] = 0;
                else {
                    r[j] = lset;
                    lset--;
                }
            } else if(d[j] == 'R') {
                lset = 0;
                r[j] = INT_MAX;
            } else if(d[j] == 'L') {
                lset = -1;
                r[j] = INT_MAX;
            }
        }
        string temp="";
        for(int i=0;i<d.size();i++) {
            if(l[i] == 0 || r[i] == 0) {
                int res = (l[i]+r[i])*(-1);
                if(res<0) temp+='R';
                else if(res>0) temp+='L';
                else temp+='.';
            } else if(l[i] == INT_MAX || r[i]==INT_MAX) {
                temp+=d[i];
            } else {
                int res = l[i]+r[i];
                if(res<0) temp+='R';
                else if(res>0) temp+='L';
                else temp+='.';
            }
        }
        return temp;
    }
};
