        int n = s.length();
        vector<int>L(n,INT_MAX);
        vector<int>R(n,INT_MAX);
        if(s[n-1]=='L')L[n-1]=0;
        for (int i = n-2; i >=0; i--)
        {
            if(s[i]=='R')
            continue;
            if(s[i]=='L')L[i]=0;
            else if(s[i]=='R')L[i]=INT_MAX;
            else L[i]=L[i+1]+1;
