/**
 * @param {string} dominoes
 * @return {string}
 */
var pushDominoes = function(dominoes) {
    const dom = dominoes.split('');
    const n = dom.length;
    let queue = [];

    for (let i = 0; i < n; i++) {
        if (dom[i] !== '.') {
            queue.push([i, dom[i]]);
        }
    }

    while (queue.length) {
        const next = [];

        for (let idx = 0; idx < queue.length; idx++) {
            const [i, d] = queue[idx];
            if (d === 'L' && i > 0 && dom[i - 1] === '.') {
                next.push([i - 1, 'L']);
                dom[i - 1] = 'L';
            } else if (d === 'R' && i + 1 < n && dom[i + 1] === '.') {
                if (i + 2 < n && dom[i + 2] === 'L') {
                    idx++;
                } else {
                    next.push([i + 1, 'R']);
                    dom[i + 1] = 'R';
                }
            }
        }

        queue = next;
    }

    return dom.join('');
};
