class Solution {
public:
	string pushDominoes(string d) {
		int right=-1;
		string s="";
		vector<pair<int,int>>v;
		for(int i=0;i<d.size();i++)
		{
			s+='.';
			if(d[i]=='R')
			{
				right=i;
			}
			if(right!=-1 && d[i]=='L')
			{
				v.push_back({right,i});
				for(int j=right;j<=i;j++)
				{
					d[j]='-';
				}
				right=-1;
			}
		}
		// In vector of pair we store the indexes which are right and immediate left after right 
		// we store vector members and in between them with '-' sign to differentiate them 
		for(int i=0;i<v.size();i++)
		{
			int a=v[i].first,b=v[i].second;
			//cout<<a<<" "<<b<<endl;
			while(a<b)
			{
				s[a]='R';
				s[b]='L';
				a++;
				b--;
			}
		}
		// we store the positions of vector members in the another string
		for(int i=0;i<d.size();i++)
		{
			if(d[i]=='R')
			{
				int x=i;
				while(x<d.size() && d[x]!='-')
				{
					s[x]='R';
					x++;
				}
				i=x;
			}
		}
		// traverse from right whichever comes right goes to right before the vector members
		for(int i=d.size()-1;i>=0;i--)
		{
			if(d[i]=='L')
			{
				int x=i;
				while(x>=0 && d[x]!='-')
				{
					s[x]='L';
					x--;
				}
				i=x;
			}
		}
	    // traverse from left whichever comes left goes to left before the vector members
		return s;
	}
};

