func pushDominoes(dominoes string) string {
    // if len is 0 or 1 nothing can be changed, return input
    if len(dominoes) <= 1 || !strings.ContainsRune(dominoes, '.'){
        return dominoes
    }

    left := 0
    // looking for first non-"." character
    right, lc := nextChar(dominoes)
    if right < 0 { // if not found, return input
        return dominoes
    }

    if lc == "L" { // if first "L", all on the left should be "L"
        dominoes = strings.Replace(dominoes, ".", "L", right)
    } 

    res := dominoes[:right+1] // copy part till first non-"." with needed replacement
    // fmt.Println("res before = ", res)

    rc := lc
    finish := true // flag to understand if we still have what to repeat, can be replaced with check of "res" length
    for finish{
        finish = false
        lc = rc
        left += right+1
        // fmt.Println("dominoes[left:] = ", dominoes[left:])
        right, rc = nextChar(dominoes[left:])
        if right < 0 && lc == "L" { // if last char was "L" the rest is "." without changes
            res += strings.Repeat(".", len(dominoes) - left)
            // fmt.Println("break L")
            break
        } else if right < 0 && lc == "R" { // if last char was "R" the rest is "R" 
            // fmt.Println("break R")
            res += strings.Repeat("R", len(dominoes) - left)
            break
        } else if rc == lc { // if same like "L..L" or "R..R", all between should be same
            // fmt.Println("replaced same")
            res += strings.Repeat(rc, right+1)
            // fmt.Println("res after replaced same = ", res)
            finish = true
        } else if lc == "R" && rc == "L"  { // if kind of "R..L", then half "R" and half "L" => "RRLL"
            half := right / 2
            res += strings.Repeat(lc, half)
            if right % 2 == 1 { // if odd, then middle isn't changed: "R...L" => "RR.LL"
                res += "."
            }
            res += strings.Repeat(rc, half+1) // additionally copy right "L"
            // fmt.Println("replaced halfs")
            finish = true
        } else { //in case of "L..R" copy with no changes
            res += dominoes[left:left+right+1]
            // fmt.Println("copy with no changes between L and R")
            // fmt.Println("res after copy with no changes = ", res)
            finish = true
        }
    }

    return res
}

func nextChar(s string)(int, string){
    leftL := strings.IndexByte(s,'L')
    leftR := strings.IndexByte(s,'R')
    if leftL>=0 && leftL < leftR || leftL>=0 && leftR<0{
        // fmt.Println("next = ", leftL,string(s[leftL]))
        return leftL,string(s[leftL])
    } else if leftR>=0 {
        // fmt.Println("next = ", leftR,string(s[leftR]))
            return leftR,string(s[leftR])
    } else {
        // fmt.Println("next = ", -1, "")
        return -1,""
    }
}
