func pushDominoes(dominoes string) string {
    arr := make([]byte, len(dominoes)+2)
    N := len(arr)
    arr[0] = 'L'
    arr[N-1] = 'R'
    for i := 0; i < len(dominoes); i++ {
        arr[i+1] = dominoes[i]
    }

    start := 0
    for end := 0; end < N; end++ {
        if arr[end] != '.' {
            convert(arr, start, end)
            start = end
        }
    }

    return string(arr[1:N-1])
}

func convert(arr []byte, start, end int) {
    if arr[start] == arr[end] {
        // Case of L...L or R....R
        for i := start+1; i < end; i++ {
            arr[i] = arr[start]
        }
    } else if arr[start] == 'R' && arr[end] == 'L' {
        // Case of R...L
        for i := start+1; i < end; i++ {
            if i-start < end-i {
                arr[i] = arr[start]
            } else if i-start > end-i {
                arr[i] = arr[end]
            } /* else {
                // If i is equal distance from start and end, leave it '.'
            } */
        }
    } /* else {
        // Case of L...R
        // Nothing needs to be done
    } */
}
