public class Solution {
    public string PushDominoes(string dominoes) {
        char[] elements = dominoes.ToCharArray();
        int length = elements.Length;
        int[] forces = new int[length];

        int force = 0;
        for(int i = 0; i < length; i++) {
            if(elements[i] == 'R') {
                force = length;
            } else if(elements[i] == 'L') {
                force = 0;
            } else {
                force = Math.Max(force - 1, 0);
            }

            forces[i] += force;
        }

        force = 0;
        for(int i = length - 1; i >= 0; i--) {
            if(elements[i] == 'L') {
                force = length;
            } else if(elements[i] == 'R') {
                force = 0;
            } else {
                force = Math.Max(force - 1, 0);
            }

            forces[i] -= force;
        }

        StringBuilder builder = new StringBuilder();
        foreach(int f in forces) {
            if(f > 0) {
                builder.Append('R');
            } else if(f < 0) {
                builder.Append('L');
            } else {
                builder.Append('.');
            }
        }

        return builder.ToString();
    }
}
