public:
    string pushDominoes(string s) {
        vector<bool> vis(s.size(),0); // to track the processed indices
        vector<int> left(s.size(),INT_MAX);  // to store time of left force on index i
        vector<int> right(s.size(),INT_MAX);  // to store time of right force on index i
        queue<int> q;  // to store the under process indices

        for(int i = 0; i<s.size();++i){  // initialize all the initial state of
            if(s[i]=='L'){               //  left and right with time 0
               q.push(i); vis[i] = 1; left[i] = 0;
            }
            if(s[i]=='R'){
               q.push(i); vis[i] = 1; right[i] = 0;
            }
        }

        while(!q.empty()){
            int t = q.size();
            while(t--){
                int i = q.front(); q.pop();
                if(left[i]<right[i]){
                    s[i]='L'; 
                    if(i-1>=0){  // state 'L' will always push its previous index to left
                        left[i-1]=min(left[i]+1,left[i-1]);  // store the min time of left force
                        if(vis[i-1]==0){
                            vis[i-1]=1; q.push(i-1);
                        }
                    }
                }
                else if(right[i]<left[i]){ 
                    s[i]='R';
                    if(i+1<s.size()){   // state 'R' will always push its next index to right
                        right[i+1] = min(right[i]+1,right[i+1]);   // store the min time of right force
                        if(vis[i+1]==0){
                            vis[i+1]=1; q.push(i+1);
