class Solution:
    def pushDominoes(self, dominoes: str) -> str:
        arr = [[position, 1] for position in dominoes] #form = [[falling direction, seconds needed to fall]]
            #R means its already falling to right 
            #L means its already falling to left
            #r means its standing still but the dominoe next to it is potentially falling to right
            #we need r because when there is dominoes falling from left this r may be changed to L or .
            #it will be changed to . if the dominoe falling to L is as far from it as the domnoe falling to right
            #it will be changed to L if the dominoe falling to L is closer to it than the one falling to right
            #by closer we mean it takes less seconds
            #the array we are keeping stores seconds needed for each dominoe to fall to a direction
            #in the case of . the seconds are trash meaning we don't really care about them
            #in the end we change all dominoes with r to R
            #NOTE: this code may be simplified but i am lazy to simplify it :P

        for right in range(0,len(dominoes) - 1):
            next_direction, next_seconds = arr[right + 1]
            curr_direction, curr_seconds = arr[right]
            if curr_direction in ("R", "r") and next_direction == ".":
                arr[right + 1] = ['r', curr_seconds + 1]

        for left in range(len(dominoes) - 1, 0, -1):
            next_direction, next_seconds = arr[left - 1]
            curr_direction, curr_seconds = arr[left]
            if curr_direction in ("L", 'l'):
                if next_direction == '.':
                    arr[left - 1] = ['L', curr_seconds + 1]
                elif next_direction == "r" and curr_seconds + 1 < next_seconds:
                    arr[left - 1] = ['L', curr_seconds + 1]
                elif next_direction == "r" and curr_seconds + 1 == next_seconds:
                    arr[left - 1] = ['.', 1]
        res = ''
        for i in range(0,len(arr)):
            direction = arr[i][0]
            if direction == "r":
                res += "R"
            else:
                res += direction
        return res

