class Solution {
public:
    string pushDominoes(string dominoes) {
        //method 2 based on distance
        int n=dominoes.size();
        vector<int> right(n,INT_MAX),left(n,INT_MAX);

        //fill right
        if(dominoes[0]=='R') right[0]=0;
        for(int i=1;i<n;i++)
        {
            if(dominoes[i]=='L') continue;
            else if(dominoes[i]=='R') right[i]=0;
            else if(right[i-1]!=INT_MAX) right[i]=right[i-1]+1;
            else right[i]=INT_MAX;
        }
        //fill left
        if(dominoes[n-1]=='L') left[n-1]=0;
        for(int i=n-2;i>=0;i--)
        {
            if(dominoes[i]=='R') continue;
            else if(dominoes[i]=='L') left[i]=0;
            else if(left[i+1]!=INT_MAX) left[i]=left[i+1]+1;
            else left[i]=INT_MAX;
        }

        for(int i=0;i<n;i++)
        {
            if(left[i]!=right[i])
            {
                dominoes[i]=left[i]<right[i]?'L':'R';
            }
        }
        return dominoes;
    }
};
