    int maximalRectangle(vector<vector<char>>& matrix) {



        int n = matrix.size();

        if(n==0){
            return 0;
        }


        int m = matrix[0].size();

        int dup[n][m];

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(matrix[i][j]=='0'){
                    dup[i][j]=0;
                }
                else{
                    dup[i][j]=1;
                }
            }
