    int maxSumMinProduct(vector<int>& nums) {
        int n = nums.size();

        vector<int> nsl(n);
        vector<int> psl(n);

        int mod = 1000000007;


        stack<int> s;
        for(int i=n-1;i>=0;i--){
            while(!s.empty() && nums[s.top()]>=nums[i]){
                s.pop();
            }
            nsl[i] = s.empty() ? n: s.top();
            s.push(i);
        }

        while(!s.empty()){
            s.pop();
        }

        for(int i=0;i<n;i++){
            while(!s.empty() && nums[s.top()]>=nums[i]){
                s.pop();
            }
            psl[i] = s.empty() ? -1: s.top();
            s.push(i);
        }

        long long prefix[n+1];
        prefix[0]=0;
        for(int i=1;i<=n;i++){
            prefix[i] = prefix[i-1]+nums[i-1];
        }

        long long ans = INT_MIN;
        for(int i=0;i<n;i++){
            ans = max(ans,nums[i]*(prefix[nsl[i]]-prefix[psl[i]+1]));
        }

        return ans%mod;

    }
};

```

