    const nsr = new Array(n).fill(0);
    const nsl = new Array(n).fill(0);

    const stack = [];

    for (let i = n - 1; i >= 0; i--) {
        while (stack.length !== 0 && heights[i] <= heights[stack[stack.length - 1]]) {
                stack.pop();
