                while(st.size() > 0 && heights[st.top()] >= heights[i])
                {
                    int vertical = heights[st.top()];
                    st.pop();

                    int right_upper_bound = st.size() > 0 ? st.top() : -1;
                    int horizontal=(i-right_upper_bound-1);
                    if(vertical * horizontal  > ret)
                        ret = vertical * horizontal;
                }
                st.push(i);
            }

            return ret;
        }
    };
