class Solution:
    def largestRectangleArea(self, heights: List[int]) -> int:
        stack = []
        max_area = 0
        l = len(heights)
        for i, n in enumerate(heights):
            if not stack or stack[-1][1] <= n:
                stack.append((i, n))
            else:
                while stack and stack[-1][1] > n:
                    j, m = stack.pop()
                    max_area = max(max_area, (i - j) * m)
                stack.append((j, n))

        while stack:
            j, m = stack.pop()
            max_area = max(max_area, (l - j) * m)
        return max_area
