class Solution {
    public int largestRectangleArea(int[] heights) {
        int len = heights.length;
        if(len == 0) return 0;
        return  helper(heights, len);
    }
    private int helper(int height[], int len){
        //We will check Nearest smallest to the left
        Stack <Integer> NSL = new Stack <>();
        //To check Nearest smallest to the right
        Stack <Integer> NSR = new Stack<>();
        int left[] = new int[len];
        int right[] = new int[len];
        //NSL
        for(int i = 0; i < len; i++){            
           if(NSL.isEmpty()) left[i] = -1;
           else if(!NSL.isEmpty() && height[NSL.peek()] >= height[i]){
               while(!NSL.isEmpty() && height[NSL.peek()] >= height[i])
                   NSL.pop();
               if(NSL.isEmpty()) left[i] = -1;
               else left[i] = NSL.peek();
           }else left[i] = NSL.peek();
           // System.out.print("\t"  + left[i]); 
           NSL.push(i); 
        }
        // System.out.print("\n");
        //NSR
        for(int i = len - 1; i >= 0; i--){
            if(NSR.isEmpty()) right[i] = len;
            else if(!NSR.isEmpty() && height[NSR.peek()] >= height[i]){
               while(!NSR.isEmpty() && height[NSR.peek()] >= height[i])
                   NSR.pop();
               if(NSR.isEmpty()) right[i] = len;
               else right[i] = NSR.peek();
           }else right[i] = NSR.peek();
           // System.out.print("\t" + right[i]);  
           NSR.push(i); 
        }
       int result = 0;
//         To find max distance from smallest element from left and smallest to it right
        for(int i = 0; i < len; i++){
          result =  Math.max(result, (right[i] - left[i] - 1) * height[i]); 
        }
        return result;
    }
}
