class Solution {
public:
    int largestRectangleArea(vector<int>& nums) {
        int n = nums.size();
        vector<int> nsl,nsr;
        stack<pair<int,int>> s1,s2;
        //finding nearest smaller element to the left
        for(int i=0;i<nums.size();i++)
        {
            if(s1.empty())
                //if there are no elements which are smaller than current, push -1
                nsl.push_back(-1);
            else{
                while(!s1.empty() && s1.top().first>=nums[i])
                    s1.pop();
                if(s1.empty())
                    nsl.push_back(-1);
                else
                    nsl.push_back(s1.top().second);
            }
            s1.push({nums[i],i});
        }
        //finding nearest smaller element to the right
        for(int i=n-1;i>=0;i--)
        {
            if(s2.empty())
                //if there are no elements which are smaller than current,push array size
                nsr.push_back(n);
            else{
                while(!s2.empty() && s2.top().first>=nums[i])
                    s2.pop();
                if(s2.empty())
                    nsr.push_back(n);
                else
                    nsr.push_back(s2.top().second);
            }
            s2.push({nums[i],i});
        }
        //as the nsr vector is constructed backwards, reverse it
        reverse(nsr.begin(),nsr.end());
        int res = INT_MIN;
        int val=0;
        for(int i=0;i<nums.size();i++)
        {
            /*
			-> for left side count (current_index - previous smaller element's index)
            -> for right side count(next smaller element's index - current_index)
            -> minus 1 because we are taking curr index twice
            -> sum the values
            ->multiply sum with height of the curr_index element
            -> consider maximum between res and val      
			*/
            val = ((i-nsl[i])+(nsr[i]-i-1))*nums[i];
            res = max(res,val);
        }
        return res;
    }
};
