public int largestRectangleArea(int[] hs) {
        int res = 0;
        Stack<Integer> st = new Stack<>();
        for (int i = 0; i <= hs.length; i++) {
            int h = i == hs.length ? 0 : hs[i];
            while (!st.isEmpty() && h < hs[st.peek()]){
                int tp = st.pop();
                res = Math.max(res, hs[tp] * (st.isEmpty() ? i : i - 1 - st.peek()));
            }
            st.push(i);
        }
        return res;
    }
