class Solution {
    public int largestRectangleArea(int[] heights) {
        int maxarea=Integer.MIN_VALUE;
        int nsl[]=new int[heights.length];
        int nsr[]=new int[heights.length];
        Stack<Integer> s=new Stack<>();
