        stack<pair<int,int>> st2;
        int n=heights.size();
        for(int i=0;i<n;i++)                               //NSL
        {
            if(st1.size()==0)nsl.push_back(-1);
            else if(st1.size()>0 && st1.top().first<heights[i])nsl.push_back(st1.top().second);
            else if(st1.size()>0 && st1.top().first>=heights[i])
            {
                while(st1.size()>0 && st1.top().first>=heights[i])
                    st1.pop();
                if(st1.size()==0)nsl.push_back(-1);
                else nsl.push_back(st1.top().second);
            }
            st1.push({heights[i],i});
        }
        int temp=n;
        for(int i=n-1;i>=0;i--)                                                    //NGR
        {
            if(st2.size()==0)nsr.push_back(temp);
            else if(st2.size()>0 && st2.top().first<heights[i])nsr.push_back(st2.top().second);
            else if(st2.size()>0 && st2.top().first>=heights[i])
