class Solution {
public:
    int largestRectangleArea(vector<int>& heights) {
        int n = heights.size();
        stack<int> st; //stack to keep track of position
        vector<int> left_bound(n,0),right_bound(n,0); //arrays to keep the left and right boundary of the rectabgles

        //Finding the left boundary for each possible rectangle
        for(int i=0;i<n;i++)
        {
            while(!st.empty() && heights[i]<=heights[st.top()])
            {
                st.pop();
            }
            if(st.empty())
            {
                left_bound[i]=0;
            }
            else
            {
                left_bound[i] = st.top()+1;
            }
            st.push(i);
        }
        //Clear the stack for re-use
        while(!st.empty())
        {
            st.pop();
        }

        //Finding the right boundary for each possible rectangle
        for(int i=n-1;i>=0;i--)
        {
            while(!st.empty() && heights[i]<=heights[st.top()])
            {
                st.pop();
            }
            if(st.empty())
            {
                right_bound[i] = n-1;
            }
            else
            {
                right_bound[i] = st.top()-1;
            }
            st.push(i);
        }

        //Calculating all possible rectangle areas using left and right boundary of each rectangle
        int maxi = 0, area;
        for(int i=0;i<n;i++)
        {
            area = (right_bound[i]-left_bound[i]+1)*heights[i];
            maxi = max(maxi, area);
        }
        return maxi;

    }
};
