```
// there will be atleast one full bar included in the rectangle
// idea is to calculate maximum  area taking each bar as the fully included bar 
class Solution {
public:
    int largestRectangleArea(vector<int>& heights) {
        int n = heights.size();
        stack<int> s;
        int max_area = 0;
        int area_with_top = 0;
        int top = 0, i = 0;
        while(i < n)
        {
            if(s.empty() || heights[s.top()] <= heights[i])
            {
                s.push(i++);
            }
            else
            {   
                top = s.top();
                s.pop();
                // heights[top] = fully included bar
                // heights[i] = nearest right bar with height < current bar
                // heights[s.top()] = nearest left bar with height < current bar
                area_with_top = heights[top] * (s.empty() ? i : i - s.top() - 1);
                max_area = max(max_area, area_with_top);
            }
        }
        while (!s.empty())
        {
            top = s.top();
            s.pop();
            // s.empty() means it is the smallest bar 
            area_with_top = heights[top] * (s.empty() ? i : i - s.top() - 1);
            max_area = max(max_area, area_with_top);
        }
        return max_area;
    }
};
