class MinTree:
    def __init__(self, arr,i,j):
        self.arr = arr
        self.left = None
        self.right = None
        self.i = i
        self.j = j
        self.mid = None
        self.min = None
        if j-i == 1:
            self.min = i
        else:
            self.mid = (i+j)//2
            self.left = MinTree(arr, i, self.mid)
            self.right = MinTree(arr, self.mid, j)

    def min_val(self):
        # print('min_val', self.mid, self.min)
        if self.min is None:
            self.min = min((self.left.min_val(), self.right.min_val()), key=self.arr.__getitem__)
        return self.min

    def __call__(self, fro, to):
        # print('call', self.mid, fro, to)
        if self.mid is None:
            return self.min_val()
        if fro <= self.i and self.j <= to:
            return self.min_val()
        if to <= self.mid:
            return self.left(fro, to)
        if fro >= self.mid:
            return self.right(fro, to)
        return min((self.left(fro, to), self.right(fro, to)), key=self.arr.__getitem__)



class Solution:
    def largestRectangleArea(self, heights):
        """
        :type heights: List[int]
        :rtype: int
        """
        if not heights:
            return 0
        min_idx = MinTree(heights, 0, len(heights))
        def f(fro, to):
            if to-fro <= 0:
                return 0
            if to-fro == 1:
                return heights[fro]
            mid = min_idx(fro, to)
            # print(fro, mid, to)
            return max(f(fro, mid), f(mid+1, to), heights[mid] * (to-fro))
        return f(0, len(heights))
