#include<stack>

class Solution {
public:
    //function to find the index of next smallerNumber
    vector<int> nextSmallNumberIndex(vector<int>& v){
        int n=v.size(),num=0;
        vector<int> ans(n);
        stack<int> s;
        s.push(-1);
        for(int i=n-1;i>=0;i--){
            num=v[i];
            while(s.top()!=-1 && v[s.top()]>=num){
                s.pop();
            }
            ans[i]=s.top();
            s.push(i);
        }
        return ans;

    }
    //function to find the index of prev smallerNumber
    vector<int> prevSmallNumberIndex(vector<int>& v){
        int n=v.size(),num=0;
        vector<int> ans(n);
        stack<int> s;
        s.push(-1);
        for(int i=0;i<n;i++){
            num=v[i];
            while(s.top()!=-1 && v[s.top()]>=num){
