class Solution {
public:
    int largestRectangleArea(vector<int>& heights) {
        stack<int> st;

        int n=heights.size(),ans=0;
        for(int i=0;i<=n;i++){

            int h=(i==n)? 0 :heights[i];
            while(!st.empty() && heights[st.top()]>=h){
                int index=st.top();
                st.pop();
                int area=st.empty() ? i*heights[index]:(i-1-st.top())*heights[index];
                ans=max(area,ans);

            }
            st.push(i);

        }
        return ans;
    }
};
