class Solution {
public:
    int largestRectangleArea(vector<int>& heights) {
        int area = 0;
        stack<int>st;
        heights.push_back(0);
        int n = heights.size();
        for(int i = 0 ; i < n ; i++){
            while(!st.empty() && heights[st.top()] > heights[i]){
                int h = heights[st.top()];
                st.pop();
                 int r = st.empty() ? -1 : st.top();
                 area = max(area, (i - r -1) * h);
            }
            st.push(i);
        }
