class Solution {
public:
    int largestRectangleArea(vector<int>& heights) {
        stack <int> st;
        int ans = 0, n = heights.size(), i = 0;

        while(i<n){
            while(!st.empty() && heights[st.top()]> heights[i]){
                int t = st.top();
                int h = heights[t];
                st.pop();
                if(st.empty()){
                    ans = max(ans, h*i);
                }
                else{
                    int len = i - st.top() - 1;
                    ans = max(ans, h*len); 
                }
            }
            st.push(i);
            i++;
        }
        return ans;
    }
};




