    private int findMaxAreaRecursive(int[] heights, int start, int end) {
        if (start > end) {
            return 0;
        }
        if (start == end) {
            return heights[start];
        }
        if (end == (start + 1)) {
            return Math.max(2 * Math.min(heights[start], heights[end]), Math.max(heights[start], heights[end]));
        }

        int minIndex = findMin(heights, start, end);
        int areaHere = heights[minIndex] * (end + 1 - start);

        int maxLeft;
        int maxRight;

        maxRight = findMaxAreaRecursive(heights, minIndex + 1, end);
        maxLeft = findMaxAreaRecursive(heights, start, minIndex - 1);

        return Math.max(areaHere, Math.max(maxLeft, maxRight));
    }

    /**
     * Find the index of the shortest bar - logN
     */
    private int findMin(int[] heights, int start, int end) {
        if (start == end) {
            return start;
        }
        if (end == start + 1) {
            return heights[start] >= heights[end] ? end : start;
        }

        int mid = start + (end - start) / 2;
        int leftCandidate = findMin(heights, start, mid);
        int rightCandidate = findMin(heights, mid + 1, end);
        return heights[leftCandidate] >= heights[rightCandidate] ? rightCandidate : leftCandidate;
    }`

