        const stack = [-1];
        for (let i = 0; i < n + 1; i++) {
            while (heights[i] < heights[stack[stack.length - 1]]) {
                const h = heights[stack.pop()];
                const w = i - stack[stack.length - 1] - 1;
                maxArea = Math.max(maxArea, h * w);
            }
            stack.push(i);
        }
    }

    return maxArea;    
};
