                            if(line[j + r] == line[j]) is_processed[j + r] = true;
                            r++;
                        }
                        while((j - l >= 0)&&(line[j - l] >= line[j])) l++;
                        new_area = (r + l - 1)*line[j];
                        if (new_area > area) area = new_area;
                    }
                }
            } return area;
