    {

        vector<int> pre(n) ; 
        pre[0] = -1 ;
        vector<int> next(n) ;
        next[n-1] = n ; 
        stack<int> s ; 
        s.push(0) ; 

        for(int i = 1 ; i<n ; i++)
        {
            if(dp[row][s.top()] < dp[row][i]) 
            {
                pre[i] = s.top() ; 
                s.push(i) ; 
            }
            else
            {
                while(!s.empty() && dp[row][s.top()] >= dp[row][i]) 
                {
                    s.pop() ; 
                }
                if(s.empty())
                {
                    pre[i] = -1 ; 
                    s.push(i) ;
                }
                else
                {
                    pre[i] = s.top() ; 
                    s.push(i) ; 
                }
            }
        }
        // s.clear() ; 
        while(!s.empty()) s.pop() ; 

        s.push(n-1) ; 
        for(int i = n-2 ; i>=0 ; i--)
        {
            if(dp[row][s.top()] < dp[row][i]) 
            {
                next[i] = s.top() ; 
                s.push(i) ; 
            }
            else
            {
                while(!s.empty() && dp[row][s.top()] >= dp[row][i]) 
                {
                    s.pop() ; 
                }
                if(s.empty())
                {
