                    next[i] = n ; 
                }
                else
                {
                    next[i] = s.top() ; 
                    s.push(i) ; 
                }
            }
        }
        int ans = INT_MIN ; 
        int area ; 
        for(int i = 0 ; i<n ; i++)
        {
            area = ( next[i] - pre[i] - 1) * dp[row][i] ; 
            ans = max(ans , area) ; 
        }
        return ans ; 
