    def maximalRectangle(self, matrix: List[List[str]]) -> int:

        if not matrix or not matrix[0]:
            return 0

        m = len(matrix)
        n = len(matrix[0])

        #prepping max squares array and finding largest value in array (max_num)
		max_num = 1
        for i in range(m):
            matrix[i][0] = int(matrix[i][0])

        for j in range(n):
            matrix[0][j] = int(matrix[0][j])

        for i in range(1, m):
            for j in range(1, n):
                if matrix[i][j] == '1':
                    tmp = min(matrix[i - 1][j], matrix[i][j - 1], matrix[i - 1][j - 1]) + 1
                    matrix[i][j] = tmp
                    max_num = max(max_num, tmp)
                else:
                    matrix[i][j] = 0

		#initialize array for global longest chain of each number
        max_values = [0 for i in range(max_num)]

