class Solution {
public:
    int pseudoIndex;
    int maximalRectangle(vector<vector<char>>& matrix) {
       int rows = matrix.size();
        if (rows == 0) {
            return 0;
        }
        int cols = matrix[0].size();
        if (cols == 0) {
            return 0;
        }
        vector<int>histogram(cols,0);

        int res = 0;

        for (int i = 0; i< rows; i++) {
            for (int j = 0; j< cols; j++) {
                if (matrix[i][j] == '0') {
                    histogram[j] = 0;
                }
                else {
                    histogram[j] += (matrix[i][j] - '0');
                }
            }
            int area = largestRectangleArea(histogram);

            res = max(area, res);
        }
        return res;
    }

    int largestRectangleArea(vector<int>& heights) {

        if(heights.size() == 0)
            return 0;
        //we store index of nearest smaller to left of i
        vector<int> nsl = NearestSmallerToLeft(heights);
        //we store index of nearest smaller to right of i
        vector<int> nsr = NearestSmallerToRight(heights);

        //find the max area possible
        int max_area = INT_MIN,area;
        for(int i = 0; i < heights.size(); i++)
        {
            area = (nsr[i]-nsl[i]-1)*heights[i];
            max_area = max(area,max_area);
        }
        return max_area;
    }

   vector<int> NearestSmallerToLeft(vector<int> temp)
    {
        stack<pair<int,int>> st;
        vector<int> left;
        pseudoIndex = -1;
        for(int i = 0; i < temp.size();i++)
        {
            if(st.empty())
                left.push_back(pseudoIndex);
            else if(!st.empty() && st.top().first < temp[i])
                left.push_back(st.top().second);
            else if(!st.empty() && st.top().first >= temp[i])
            {
                while(!st.empty() && st.top().first >= temp[i])
                {
                    st.pop();
                }
                if(st.empty())
                {
                    left.push_back(-1);
                }
                else left.push_back(st.top().second);
            }
        st.push({temp[i],i});
        }
    return left;
    }

    vector<int> NearestSmallerToRight(vector<int> temp)
    {
        stack<pair<int,int>> st;
        vector<int> right;
        pseudoIndex = temp.size();    
        for(int i = temp.size() - 1; i >= 0; i--)
        {
            if(st.empty())
            {
                right.push_back(pseudoIndex);
            }
            else if(!st.empty() && st.top().first < temp[i])
                right.push_back(st.top().second);
            else if(!st.empty() && st.top().first >= temp[i])
            {
                while(!st.empty() && st.top().first >= temp[i])
                    st.pop();
                if(st.empty())
                    right.push_back(pseudoIndex);
                else 
                    right.push_back(st.top().second);
            }
        st.push({temp[i],i});
        }
        reverse(right.begin(),right.end());
        return right;
    }
};
